const budgetMap = new Map();
for (const item of $input.all()) {
  const r = item.json;
  if (!budgetMap.has(r.id)) {
    budgetMap.set(r.id, {
      id: r.id,
      month: r.month,
      category: r.category,
      budget_amount: parseFloat(r.budget_amount) || 0,
      spent: parseFloat(r.spent) || 0,
      remaining: parseFloat(r.remaining) || 0
    });
  }
}
return [{ json: { budgets: Array.from(budgetMap.values()) } }];
