parameters.query:
-- Upsert budget (insert or update if exists)
INSERT INTO finance.budgets (month, category, budget_amount)
VALUES (
  DATE_TRUNC('month', CURRENT_DATE),
  '{{ $json.body.category }}',
  {{ $json.body.amount }}
)
ON CONFLICT (month, category) 
DO UPDATE SET 
  budget_amount = {{ $json.body.amount }}
RETURNING id, month, category, budget_amount;

parameters.responseBody:
={
  "success": true,
  "message": "Budget set for {{ $('Upsert Budget').item.json.category }}",
  "data": {
    "budget": {
      "id": {{ $('Upsert Budget').item.json.id }},
      "month": "{{ $('Upsert Budget').item.json.month }}",
      "category": "{{ $('Upsert Budget').item.json.category }}",
      "budget_amount": {{ $('Upsert Budget').item.json.budget_amount }},
      "spent": {{ $('Get Category Spending').item.json.spent }},
      "remaining": {{ $('Upsert Budget').item.json.budget_amount - $('Get Category Spending').item.json.spent }}
    }
  }
}
