const body = $input.first().json.body || $input.first().json;
const esc = (v) => v == null ? null : String(v).replace(/'/g, "''");

const title = body.title ? "'" + esc(body.title) + "'" : 'NULL';
const startAt = body.start_at ? "'" + esc(body.start_at) + "'::timestamptz" : 'NULL';
const endAt = body.end_at ? "'" + esc(body.end_at) + "'::timestamptz" : 'NULL';
const isAllDay = body.is_all_day ? 'true' : 'false';
const calendarName = body.calendar_name ? "'" + esc(body.calendar_name) + "'" : "'Calendar'";
const location = body.location ? "'" + esc(body.location) + "'" : 'NULL';
const notes = body.notes ? "'" + esc(body.notes) + "'" : 'NULL';
const origin = body.origin ? "'" + esc(body.origin) + "'" : "'nexus_api'";

if (!body.start_at || !body.end_at) {
  return { json: { sql: 'SELECT 1', error: 'start_at and end_at required' } };
}

const sql = `INSERT INTO raw.calendar_events (
  event_id, title, start_at, end_at, is_all_day, calendar_name,
  location, notes, source, origin, sync_status
) VALUES (
  'nexus-' || gen_random_uuid()::text,
  ${title}, ${startAt}, ${endAt}, ${isAllDay}, ${calendarName},
  ${location}, ${notes}, 'nexus_api', ${origin}, 'pending_push'
) RETURNING id, event_id, title, sync_status;`;

return { json: { sql } };

const body = $input.first().json.body || $input.first().json;
const esc = (v) => v == null ? null : String(v).replace(/'/g, "''");

if (!body.id && !body.event_id) {
  return { json: { sql: 'SELECT 1', error: 'id or event_id required' } };
}

const sets = [];
if (body.title !== undefined) sets.push(`title = '${esc(body.title)}'`);
if (body.start_at !== undefined) sets.push(`start_at = '${esc(body.start_at)}'::timestamptz`);
if (body.end_at !== undefined) sets.push(`end_at = '${esc(body.end_at)}'::timestamptz`);
if (body.is_all_day !== undefined) sets.push(`is_all_day = ${body.is_all_day ? 'true' : 'false'}`);
if (body.calendar_name !== undefined) sets.push(`calendar_name = '${esc(body.calendar_name)}'`);
if (body.location !== undefined) sets.push(`location = ${body.location ? "'" + esc(body.location) + "'" : 'NULL'}`);
if (body.notes !== undefined) sets.push(`notes = ${body.notes ? "'" + esc(body.notes) + "'" : 'NULL'}`);

sets.push("sync_status = 'pending_push'");

const where = body.id ? `id = ${parseInt(body.id)}` : `event_id = '${esc(body.event_id)}' AND source = 'ios_eventkit'`;

const sql = `UPDATE raw.calendar_events SET ${sets.join(', ')} WHERE ${where} AND deleted_at IS NULL RETURNING id, event_id, sync_status;`;

return { json: { sql } };

const body = $input.first().json.body || $input.first().json;
const esc = (v) => v == null ? null : String(v).replace(/'/g, "''");

if (!body.id && !body.event_id) {
  return { json: { sql: 'SELECT 1', error: 'id or event_id required' } };
}

const where = body.id ? `id = ${parseInt(body.id)}` : `event_id = '${esc(body.event_id)}' AND source = 'ios_eventkit'`;

const sql = `UPDATE raw.calendar_events SET sync_status = 'deleted_local', deleted_at = CURRENT_TIMESTAMP WHERE ${where} AND deleted_at IS NULL RETURNING id, event_id, sync_status;`;

return { json: { sql } };

const body = $input.first().json.body || $input.first().json;
const confirmations = body.confirmations || [];
const esc = (v) => v == null ? null : String(v).replace(/'/g, "''");

if (!Array.isArray(confirmations) || confirmations.length === 0) {
  return { json: { sql: 'SELECT 1', count: 0 } };
}

const statements = confirmations.filter(c => c.action !== 'deleted').map(c => {
  if (!c.db_id && !c.db_event_id) return null;
  const sets = ["sync_status = 'synced'"];
  if (c.eventkit_id) sets.push(`event_id = '${esc(c.eventkit_id)}'`);
  if (c.eventkit_modified_at) sets.push(`eventkit_modified_at = '${esc(c.eventkit_modified_at)}'::timestamptz`);
  sets.push('last_seen_at = CURRENT_TIMESTAMP');

  const where = c.db_id ? `id = ${parseInt(c.db_id)}` : `event_id = '${esc(c.db_event_id)}'`;
  return `UPDATE raw.calendar_events SET ${sets.join(', ')} WHERE ${where};`;
}).filter(s => s !== null);

const deletionConfirms = confirmations.filter(c => c.action === 'deleted').map(c => {
  const where = c.db_id ? `id = ${parseInt(c.db_id)}` : `event_id = '${esc(c.db_event_id)}'`;
  return `DELETE FROM raw.calendar_events WHERE ${where} AND sync_status = 'deleted_local';`;
});

const sql = [...statements, ...deletionConfirms].join('\n');

return { json: { sql: sql || 'SELECT 1', count: confirmations.length } };
