// Extract environment sensors from HA
const states = $input.first().json;
const now = new Date().toISOString();
const today = now.split('T')[0];

// Map HA sensors to areas and metrics
const temperatureSensors = {
  'sensor.pivpn_temperature': 'server_room',
  'sensor.ai_temperature': 'server_room',
  'sensor.nas_temperature': 'server_room'
};

const humiditySensors = {
  // Add humidity sensors if available
};

const metrics = [];

for (const state of states) {
  // Temperature sensors
  const area = temperatureSensors[state.entity_id];
  if (area && state.state !== 'unavailable' && state.state !== 'unknown') {
    const temp = parseFloat(state.state);
    if (!isNaN(temp)) {
      metrics.push({
        recorded_at: state.last_updated || now,
        date: today,
        area: area,
        temperature_c: temp,
        entity_id: state.entity_id
      });
    }
  }

  // Humidity sensors (if available)
  const humidityArea = humiditySensors[state.entity_id];
  if (humidityArea && state.state !== 'unavailable' && state.state !== 'unknown') {
    const humidity = parseFloat(state.state);
    if (!isNaN(humidity)) {
      metrics.push({
        recorded_at: state.last_updated || now,
        date: today,
        area: humidityArea,
        humidity_pct: humidity,
        entity_id: state.entity_id
      });
    }
  }
}

return metrics;
