parameters.responseBody:
={
  "success": true,
  "message": "Logged ${{ $json.amount }} at {{ $json.merchant_name }}",
  "data": {
    "transaction": {
      "id": {{ $('Insert Transaction').item.json.id }},
      "merchant_name": "{{ $json.merchant_name }}",
      "amount": {{ $json.amount }},
      "category": "{{ $json.category }}",
      "is_grocery": {{ $json.is_grocery }},
      "is_restaurant": {{ $json.is_restaurant }}
    },
    "total_spent": {{ $('Get Daily Summary').item.json.total_spent }},
    "category_spent": {{ $('Get Daily Summary').item.json.category_spent }}
  }
}
