parameters.jsCode:
const input = $input.first().json.body || $input.first().json;
const text = (input.text || '').trim();
const clientId = input.client_id || null;

if (!text || text.length < 2) {
  throw new Error('Expense description is required (min 2 chars)');
}

if (text.length > 500) {
  throw new Error('Expense description too long (max 500 chars)');
}

return {
  original_text: text,
  client_id: clientId,
  logged_at: new Date().toISOString()
};

