parameters.jsCode:
// Extract health-related sensors from HA
const states = $input.first().json;
const now = new Date().toISOString();
const today = now.split('T')[0];

// Define which sensors to track
const sensorMappings = {
  // Eufy Smart Scale - Weight
  'sensor.smart_scale_p3_weight': { metric_type: 'weight', unit: 'kg', source: 'ha_eufy_scale' },

  // Core recovery metrics (WHOOP)
  'sensor.whoop_ahmed_recovery_score': { metric_type: 'recovery', unit: 'score', source: 'whoop' },
  'sensor.whoop_ahmed_hrv': { metric_type: 'hrv', unit: 'ms', source: 'whoop' },
  'sensor.whoop_ahmed_resting_heart_rate': { metric_type: 'rhr', unit: 'bpm', source: 'whoop' },
  'sensor.whoop_ahmed_spo2': { metric_type: 'spo2', unit: '%', source: 'whoop' },
  'sensor.whoop_ahmed_skin_temperature': { metric_type: 'skin_temp', unit: 'C', source: 'whoop' },

  // Strain and activity
  'sensor.whoop_ahmed_day_strain': { metric_type: 'strain', unit: 'score', source: 'whoop' },
  'sensor.whoop_ahmed_day_calories': { metric_type: 'calories', unit: 'kcal', source: 'whoop' },
  'sensor.whoop_ahmed_day_average_heart_rate': { metric_type: 'avg_hr', unit: 'bpm', source: 'whoop' },

  // Sleep metrics
  'sensor.whoop_ahmed_sleep_performance': { metric_type: 'sleep_performance', unit: 'score', source: 'whoop' },
  'sensor.whoop_ahmed_sleep_efficiency': { metric_type: 'sleep_efficiency', unit: '%', source: 'whoop' },
  'sensor.whoop_ahmed_sleep_time_in_bed': { metric_type: 'time_in_bed', unit: 'min', source: 'whoop' },
  'sensor.whoop_ahmed_sleep_sws_time': { metric_type: 'deep_sleep', unit: 'min', source: 'whoop' },
  'sensor.whoop_ahmed_sleep_rem_sleep_time': { metric_type: 'rem_sleep', unit: 'min', source: 'whoop' },
  'sensor.whoop_ahmed_sleep_light_sleep_time': { metric_type: 'light_sleep', unit: 'min', source: 'whoop' },
  'sensor.whoop_ahmed_sleep_time_awake': { metric_type: 'awake_time', unit: 'min', source: 'whoop' },
  'sensor.whoop_ahmed_sleep_respiratory_rate': { metric_type: 'respiratory_rate', unit: 'brpm', source: 'whoop' }
};

const metrics = [];

for (const state of states) {
  const mapping = sensorMappings[state.entity_id];
  if (mapping && state.state !== 'unavailable' && state.state !== 'unknown') {
    const value = parseFloat(state.state);
    if (!isNaN(value)) {
      metrics.push({
        recorded_at: state.last_updated || now,
        date: today,
        source: mapping.source,
        metric_type: mapping.metric_type,
        value: value,
        unit: mapping.unit,
        entity_id: state.entity_id
      });
    }
  }
}

return metrics;

parameters.query:
=INSERT INTO health.metrics (recorded_at, date, source, metric_type, value, unit)
VALUES 
{{ $input.all().map(item => 
  `('${item.json.recorded_at}', '${item.json.date}', '${item.json.source}', '${item.json.metric_type}', ${item.json.value}, '${item.json.unit}')`
).join(',\n') }}
ON CONFLICT (date, metric_type, source) DO UPDATE SET
  value = EXCLUDED.value,
  recorded_at = EXCLUDED.recorded_at,
  created_at = NOW()
RETURNING id, metric_type, value;
