// Validate and prepare HA service call
const input = $input.first().json.body || $input.first().json;

const action = input.action; // toggle, turn_on, turn_off, set
const entityId = input.entity_id;
const data = input.data || {};

if (!action || !entityId) {
  throw new Error('action and entity_id are required');
}

// Determine domain and service
const domain = entityId.split('.')[0]; // switch, light, vacuum, etc.
let service = action;

// Map actions to HA services
if (action === 'toggle') {
  service = 'toggle';
} else if (action === 'turn_on') {
  service = 'turn_on';
} else if (action === 'turn_off') {
  service = 'turn_off';
} else if (action === 'set' && domain === 'light') {
  service = 'turn_on'; // set brightness via turn_on
}

// Special handling for vacuum
if (domain === 'vacuum') {
  if (action === 'start') service = 'start';
  else if (action === 'stop') service = 'stop';
  else if (action === 'return') service = 'return_to_base';
  else if (action === 'locate') service = 'locate';
}

// Build service data
const serviceData = { entity_id: entityId };
if (data.brightness !== undefined) {
  serviceData.brightness = Math.round(data.brightness * 2.55); // 0-100 to 0-255
}

return {
  domain,
  service,
  serviceData,
  originalAction: action,
  entityId
};
