// Transform HA states into structured home status
const states = $input.first().json;

// Helper to find entity
const getEntity = (entityId) => states.find(s => s.entity_id === entityId);

// Lights
const lightstrip = getEntity('light.hue_lightstrip_plus_1');
const lights = {
  hue_lightstrip: lightstrip ? {
    entity_id: 'light.hue_lightstrip_plus_1',
    state: lightstrip.state,
    brightness: lightstrip.attributes?.brightness || null,
    brightness_pct: lightstrip.attributes?.brightness ? Math.round(lightstrip.attributes.brightness / 255 * 100) : null
  } : null
};

// Switches (monitors + printer)
const leftMon = getEntity('switch.leftmonplug_socket_1');
const rightMon = getEntity('switch.rightmonplug_socket_1');
const printer = getEntity('switch.3d_printer_socket_1');

const switches = {
  left_monitor: leftMon ? { entity_id: 'switch.leftmonplug_socket_1', state: leftMon.state } : null,
  right_monitor: rightMon ? { entity_id: 'switch.rightmonplug_socket_1', state: rightMon.state } : null,
  printer: printer ? { entity_id: 'switch.3d_printer_socket_1', state: printer.state } : null
};

// Vacuum
const vacuum = getEntity('vacuum.vacuum');
const vacuumBattery = getEntity('sensor.vacuum_battery');
const vacuumStatus = vacuum ? {
  entity_id: 'vacuum.vacuum',
  state: vacuum.state,
  battery: vacuumBattery ? parseInt(vacuumBattery.state) : null,
  fan_speed: vacuum.attributes?.fan_speed || null
} : null;

// Camera
const camera = getEntity('camera.ezviz_camera');
const cameraSleep = getEntity('switch.ezviz_camera_sleep');
const cameraStatus = camera ? {
  entity_id: 'camera.ezviz_camera',
  state: camera.state,
  sleeping: cameraSleep?.state === 'on'
} : null;

// Motion/Presence (find most recent motion)
const motionSensors = states.filter(s => 
  s.entity_id.includes('motion') && 
  s.attributes?.device_class === 'motion'
);
const lastMotion = motionSensors
  .filter(s => s.state === 'on' || s.last_changed)
  .sort((a, b) => new Date(b.last_changed) - new Date(a.last_changed))[0];

const presence = {
  home: motionSensors.some(s => s.state === 'on') || true, // Default to home
  last_motion: lastMotion?.last_changed || null,
  motion_location: lastMotion?.entity_id?.replace('binary_sensor.', '').replace('_motion', '') || null
};

return {
  success: true,
  home: {
    lights,
    switches,
    vacuum: vacuumStatus,
    camera: cameraStatus,
    presence
  },
  last_updated: new Date().toISOString()
};
