parameters.jsCode:
const input = $input.first().json.body || $input.first().json;
const amount = parseFloat(input.amount);
const transaction_at = input.transaction_at || new Date().toISOString();

if (!amount || isNaN(amount)) {
  throw new Error('Amount is required (must be a valid number)');
}

if (amount <= 0) {
  throw new Error('Amount must be greater than 0');
}

if (!input.source) {
  throw new Error('Source/merchant name is required');
}

if (!input.category) {
  throw new Error('Category is required');
}

return {
  transaction_at: transaction_at,
  source: input.source,
  amount: amount,
  currency: input.currency || 'AED',
  category: input.category,
  notes: input.notes || null,
  is_recurring: input.is_recurring || false,
  client_id: input.client_id || null,
  validated_at: new Date().toISOString()
};

parameters.responseBody:
={
  "success": true,
  "message": {{ $json.length > 0 ? "'Income added: " + $json[0].merchant_name + " - " + $json[0].currency + " " + $json[0].amount + "'" : "'Income already exists (idempotent duplicate)'" }},
  "idempotent": {{ $json.length === 0 ? 'true' : 'false' }},
  "data": {
    "transaction": {{ $json.length > 0 ? JSON.stringify($json[0]) : 'null' }}
  }
}
