const rows = $input.all().map(i => i.json);

// Calculate summary
const activePlans = rows.length;
const totalRemaining = rows.reduce((sum, r) => sum + parseFloat(r.remaining_amount || 0), 0);
const dueThisWeek = rows.filter(r => r.is_due_soon).length;
const dueThisWeekAmount = rows.filter(r => r.is_due_soon).reduce((sum, r) => sum + parseFloat(r.installment_amount || 0), 0);

const currency = rows.length > 0 ? rows[0].currency : 'AED';

const plans = rows.map(r => ({
  id: r.id,
  source: r.source,
  merchant: r.merchant,
  total_amount: parseFloat(r.total_amount),
  currency: r.currency,
  installments_total: r.installments_total,
  installments_paid: r.installments_paid,
  installment_amount: parseFloat(r.installment_amount),
  purchase_date: r.purchase_date,
  next_due_date: r.next_due_date,
  final_due_date: r.final_due_date,
  status: r.status,
  remaining_payments: r.remaining_payments,
  remaining_amount: parseFloat(r.remaining_amount),
  is_overdue: r.is_overdue,
  is_due_soon: r.is_due_soon
}));

return [{ json: {
  plans,
  summary: {
    active_plans: activePlans,
    total_remaining: totalRemaining.toFixed(2),
    due_this_week: dueThisWeek,
    due_this_week_amount: dueThisWeekAmount.toFixed(2)
  }
}}];

const body = $input.first().json.body || $input.first().json;

const source = (body.source || '').toLowerCase();
const merchant = body.merchant;
const totalAmount = parseFloat(body.total_amount || body.totalAmount);
const installmentsTotal = parseInt(body.installments_total || body.installmentsTotal || 4);
const installmentAmount = totalAmount / installmentsTotal;
const currency = body.currency || 'AED';
const purchaseDate = body.purchase_date || body.purchaseDate || null;
const nextDueDate = body.next_due_date || body.nextDueDate || null;
const finalDueDate = body.final_due_date || body.finalDueDate || null;

if (!['tabby', 'tamara', 'postpay', 'other'].includes(source)) {
  return [{ json: { valid: false, error: 'source must be tabby, tamara, postpay, or other' } }];
}

if (!merchant || merchant.trim().length === 0) {
  return [{ json: { valid: false, error: 'merchant is required' } }];
}

if (!totalAmount || totalAmount <= 0) {
  return [{ json: { valid: false, error: 'total_amount must be positive' } }];
}

return [{ json: {
  valid: true,
  source,
  merchant: merchant.trim(),
  totalAmount,
  installmentsTotal,
  installmentAmount: installmentAmount.toFixed(2),
  currency,
  purchaseDate,
  nextDueDate,
  finalDueDate
} }];
