const items = $input.all();
const months = items.map(item => {
  const r = item.json;
  const breakdown = r.category_breakdown || {};
  const parsed = {};
  for (const [cat, val] of Object.entries(breakdown)) {
    parsed[cat] = parseFloat(val) || 0;
  }
  return {
    month: r.month,
    total_spent: parseFloat(r.total_spent) || 0,
    category_breakdown: parsed
  };
});
return [{ json: { monthly_spending: months } }];
