parameters.jsCode:
const input = $input.first().json.body || $input.first().json;
const events = input.events;

if (!events || !Array.isArray(events) || events.length === 0) {
  throw new Error('events array is required and must not be empty');
}

const validated = events.map(e => {
  if (!e.session_id || !e.track_title || !e.started_at) {
    throw new Error('Each event requires session_id, track_title, and started_at');
  }
  return {
    session_id: e.session_id,
    track_title: e.track_title,
    artist: e.artist || null,
    album: e.album || null,
    duration_sec: e.duration_sec ? parseInt(e.duration_sec) : null,
    apple_music_id: e.apple_music_id || null,
    started_at: e.started_at,
    ended_at: e.ended_at || null,
    source: e.source || 'apple_music',
    raw_json: JSON.stringify(e)
  };
});

return { events: validated, count: validated.length };

parameters.query:
={{ (function() {
  const events = $json.events;
  const values = events.map(e =>
    `('${e.session_id}', '${e.track_title.replace(/'/g, "''")}', ${e.artist ? "'" + e.artist.replace(/'/g, "''") + "'" : 'NULL'}, ${e.album ? "'" + e.album.replace(/'/g, "''") + "'" : 'NULL'}, ${e.duration_sec || 'NULL'}, ${e.apple_music_id ? "'" + e.apple_music_id + "'" : 'NULL'}, '${e.started_at}', ${e.ended_at ? "'" + e.ended_at + "'" : 'NULL'}, '${e.source}', '${e.raw_json.replace(/'/g, "''")}')`
  ).join(',\n');
  return `INSERT INTO life.listening_events (session_id, track_title, artist, album, duration_sec, apple_music_id, started_at, ended_at, source, raw_json)\nVALUES ${values}\nON CONFLICT (session_id, started_at) DO NOTHING\nRETURNING id, session_id, track_title, started_at;`;
})() }}

