// Extract power monitoring sensors from HA Tuya plugs
const states = $input.first().json;
const now = new Date().toISOString();
const today = now.split('T')[0];

// Map power sensors to device names (from CLAUDE.md)
const powerSensors = {
  'sensor.leftmonplug_power': 'studio_monitor_left',
  'sensor.rightmonplug_power': 'studio_monitor_right',
  'sensor.3d_printer_power': '3d_printer',
  'sensor.living_room_lamp_power': 'living_room_lamp'
};

const voltageSensors = {
  'sensor.leftmonplug_voltage': 'studio_monitor_left',
  'sensor.rightmonplug_voltage': 'studio_monitor_right',
  'sensor.3d_printer_voltage': '3d_printer',
  'sensor.living_room_lamp_voltage': 'living_room_lamp'
};

const currentSensors = {
  'sensor.leftmonplug_current': 'studio_monitor_left',
  'sensor.rightmonplug_current': 'studio_monitor_right',
  'sensor.3d_printer_current': '3d_printer',
  'sensor.living_room_lamp_current': 'living_room_lamp'
};

// Group metrics by device
const deviceMetrics = {};

for (const state of states) {
  // Power
  const powerDevice = powerSensors[state.entity_id];
  if (powerDevice && state.state !== 'unavailable' && state.state !== 'unknown') {
    const power = parseFloat(state.state);
    if (!isNaN(power)) {
      if (!deviceMetrics[powerDevice]) {
        deviceMetrics[powerDevice] = { recorded_at: state.last_updated || now, date: today, entity_id: state.entity_id };
      }
      deviceMetrics[powerDevice].power_w = power;
    }
  }

  // Voltage
  const voltageDevice = voltageSensors[state.entity_id];
  if (voltageDevice && state.state !== 'unavailable' && state.state !== 'unknown') {
    const voltage = parseFloat(state.state);
    if (!isNaN(voltage)) {
      if (!deviceMetrics[voltageDevice]) {
        deviceMetrics[voltageDevice] = { recorded_at: state.last_updated || now, date: today, entity_id: state.entity_id };
      }
      deviceMetrics[voltageDevice].voltage_v = voltage;
    }
  }

  // Current
  const currentDevice = currentSensors[state.entity_id];
  if (currentDevice && state.state !== 'unavailable' && state.state !== 'unknown') {
    const current = parseFloat(state.state);
    if (!isNaN(current)) {
      if (!deviceMetrics[currentDevice]) {
        deviceMetrics[currentDevice] = { recorded_at: state.last_updated || now, date: today, entity_id: state.entity_id };
      }
      deviceMetrics[currentDevice].current_a = current;
    }
  }
}

// Convert to array with device names
const metrics = Object.entries(deviceMetrics).map(([device, data]) => ({
  ...data,
  device_name: device
}));

return metrics;
