const input = $input.first().json;
const id = input.query?.id;
if (!id) throw new Error('id query param is required');
return { id: parseInt(id) };

const header = $('Fetch Receipt Header').first().json;
const items = $('Fetch Receipt Items').all().map(i => i.json);

return {
  success: true,
  receipt: {
    ...header,
    items: items
  }
};

const input = $input.first().json.body || $input.first().json;
if (!input.item_id || !input.food_id) {
  throw new Error('item_id and food_id are required');
}
return {
  item_id: parseInt(input.item_id),
  food_id: parseInt(input.food_id),
  is_user_confirmed: input.is_user_confirmed !== false
};

