// Build the response from all query results
const summary = $('Get Summary').first().json;
const transactions = $('Get Transactions').all();
const budgets = $('Get Budgets').all();
const categoryData = $('Get Categories').all();

// Convert category breakdown to object
const categoryBreakdown = {};
for (const item of categoryData) {
  categoryBreakdown[item.json.category] = parseFloat(item.json.total);
}

return {
  json: {
    success: true,
    data: {
      total_spent: parseFloat(summary.total_spent) || 0,
      grocery_spent: parseFloat(summary.grocery_spent) || 0,
      eating_out_spent: parseFloat(summary.eating_out_spent) || 0,
      currency: summary.currency || 'AED',
      total_income: parseFloat(summary.mtd_income) || 0,
      mtd_spend: parseFloat(summary.mtd_spend) || 0,
      recent_transactions: transactions,
      budgets: budgets,
      category_breakdown: categoryBreakdown
    }
  }
};
