parameters.jsCode:
const webhookData = $('Webhook').item.json.body;

// Get raw_event_id from the 'Log Raw Event' node output
const rawEventId = $items("Log Raw Event")?.[0]?.json?.id
  ?? $items("Log Raw Event")?.[0]?.json?.rows?.[0]?.id;

if (!rawEventId) {
  throw new Error("Missing raw_event id from 'Log Raw Event' output. Ensure INSERT ... RETURNING id.");
}

let amount = webhookData.amount;
let currency = webhookData.currency || "AED";
let parseErrors = [];

// Server-side parse from raw_text if provided
if (webhookData.raw_text) {
  const rawText = String(webhookData.raw_text);
  const amountMatch = rawText.match(/\b([\d,]+(?:\.\d+)?)\s*(aed|usd|sar|bhd|jod|egp|gbp)?\b/i);
  if (amountMatch) {
    const parsedAmount = Number(amountMatch[1].replace(/,/g, ""));
    if (Number.isFinite(parsedAmount)) {
      amount = parsedAmount;
      if (amountMatch[2]) currency = amountMatch[2].toUpperCase();
    } else {
      parseErrors.push("Could not parse amount from raw_text");
    }
  } else {
    parseErrors.push("No amount found in raw_text");
  }
}

// Normalize + validate amount
const amountNum = Number(
  typeof amount === "string" ? amount.replace(/,/g, "").trim() : amount
);

if (!Number.isFinite(amountNum) || amountNum <= 0) {
  parseErrors.push("Amount must be positive");
} else {
  amount = amountNum;
}

return [{
  json: {
    raw_event_id: rawEventId,
    client_id: webhookData.client_id,
    transaction_at: webhookData.transaction_at || new Date().toISOString(),
    source: webhookData.source || "Unknown Source",
    amount,
    currency,
    category: webhookData.category || "Income",
    notes: webhookData.notes || null,
    is_recurring: !!webhookData.is_recurring,
    parse_errors: parseErrors,
    has_errors: parseErrors.length > 0,
    raw_payload: webhookData
  }
}];

parameters.query:
-- Update raw event with validation failure
UPDATE finance.raw_events
SET validation_status = 'invalid',
    validation_errors = ARRAY[{{ $json.parse_errors.map(e => "'" + e + "'").join(',') }}]
WHERE id = {{ $json.raw_event_id }};

SELECT '{{ JSON.stringify($json.parse_errors) }}' as errors;

