// Get the parsed data from Parse Amount & Currency node
const parsed = $('Parse Amount & Currency').item.json;

// Get Insert Transaction result (current input)
const insertResult = $input.item.json;
const insertedId = insertResult?.id ?? null;

return [{
  json: {
    raw_event_id: parsed.raw_event_id,
    client_id: parsed.client_id,
    source: parsed.source,
    amount: parsed.amount,
    currency: parsed.currency,
    category: parsed.category,
    notes: parsed.notes,
    is_recurring: parsed.is_recurring,
    inserted_transaction_id: insertedId,
    was_inserted: insertedId !== null,
  }
}];

