parameters.jsCode:
// Server-side amount and currency parsing
const payload = $input.all()[0].json;
const rawEvent = payload[0];
const webhookData = $('Webhook').item.json.body;

// Parse amount from raw_text if provided, otherwise use provided amount
let amount = webhookData.amount;
let currency = webhookData.currency || 'AED';
let parseErrors = [];

// If raw_text is provided, parse it server-side (don't trust client parsing)
if (webhookData.raw_text) {
  const rawText = webhookData.raw_text.toLowerCase();

  // Extract amount (look for patterns like "23500" or "23,500.00")
  const amountMatch = rawText.match(/\b([\d,]+\.?\d*)\s*(aed|usd|sar|bhd|jod|egp|gbp)?/i);
  if (amountMatch) {
    const parsedAmount = parseFloat(amountMatch[1].replace(/,/g, ''));
    if (!isNaN(parsedAmount)) {
      amount = parsedAmount;
      if (amountMatch[2]) {
        currency = amountMatch[2].toUpperCase();
      }
    } else {
      parseErrors.push('Could not parse amount from raw_text');
    }
  } else {
    parseErrors.push('No amount found in raw_text');
  }
}

// Validate amount
if (!amount || amount <= 0) {
  parseErrors.push('Amount must be positive');
}

return {
  raw_event_id: rawEvent.id,
  raw_payload: rawEvent.raw_payload,
  client_id: webhookData.client_id,
  transaction_at: webhookData.transaction_at || new Date().toISOString(),
  source: webhookData.source || 'Unknown Source',
  amount: amount,
  currency: currency,
  category: webhookData.category || 'Income',
  notes: webhookData.notes || null,
  is_recurring: webhookData.is_recurring || false,
  parse_errors: parseErrors,
  has_errors: parseErrors.length > 0
};

parameters.query:
-- Update raw event with validation failure
UPDATE finance.raw_events
SET validation_status = 'invalid',
    validation_errors = ARRAY[{{ $json.parse_errors.map(e => "'" + e + "'").join(',') }}]
WHERE id = {{ $json.raw_event_id }};

SELECT '{{ JSON.stringify($json.parse_errors) }}' as errors;

