-- Update raw event with result
UPDATE finance.raw_events
SET validation_status = {{ $('Insert Transaction').item.json.length > 0 ? "'valid'" : "'duplicate'" }},
    parsed_amount = {{ $('Parse Amount & Currency').item.json.amount }},
    parsed_currency = '{{ $('Parse Amount & Currency').item.json.currency }}',
    related_transaction_id = {{ $('Insert Transaction').item.json.length > 0 ? $('Insert Transaction').item.json[0].id : 'NULL' }}
WHERE id = {{ $('Parse Amount & Currency').item.json.raw_event_id }};

SELECT 1;

