parameters.responseBody:
={
  "success": true,
  "message": {{ $json.length > 0 ? "'Income added: " + $('Webhook').item.json.body.source + " - " + ($('Webhook').item.json.body.currency || 'AED') + " " + $('Webhook').item.json.body.amount + "'" : "'Income already exists (idempotent duplicate)'" }},
  "idempotent": {{ $json.length === 0 ? 'true' : 'false' }},
  "data": {
    "transaction": {{ $json.length > 0 ? JSON.stringify($json[0]) : 'null' }}
  }
}
